import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import Table from 'react-bootstrap/Table';

export default function ContractConfig() {

    const [accounts, setAccounts] = useState(null);
    const [contract, setContract] = useState(null);
    const [EINRAdd, setEINRAdd] = useState(null);
    const [EGOLDAdd, setEGOLDAdd] = useState(null);


    const [addEGOLD, setAddEGOLD] = useState("");

    useEffect(() => {
        setTimeout(async () => {
            const artifact = require(`../contracts/EINR.json`);
            const _web3 = new Web3(Web3.givenProvider);
            const _accounts = await _web3.eth.requestAccounts();
            const networkID = await _web3.eth.net.getId();
            const { abi } = artifact;
            let address, contract;
            try {
                address = artifact.networks[networkID].address;
                contract = new _web3.eth.Contract(abi, address);
            } catch (err) {
                console.error(err);
            }
            setAccounts(_accounts);
            setContract(contract);
            setEINRAdd(address);

            await contract.methods.EGOLD().call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setEGOLDAdd(e);
                })
                .catch(err => console.log(err));
        }, 100)

    }, [])

    const getDataHandler = async () => {
        await contract.methods.EGOLD().call({ from: accounts[0] })
            .then(e => {
                //console.log(e);
                setEGOLDAdd(e);
            })
            .catch(err => console.log(err));
    }

    const setEGOLDHandler = (e) => {
        setAddEGOLD(e.target.value);
    }

    const setEGOLD = async () => {
        await contract.methods.setEGOLD(addEGOLD)
            .send({
                from: accounts[0]
            })
            .then(e => {
                //console.log(e);
            })
            .catch(err => console.log(err));
        getDataHandler();
        setAddEGOLD("");
    }

    return (
        <div>
            <div>
                <h2>EINR Config</h2>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Contract Name</th>
                        <th>Contract Address</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>EINR</td>
                        <td>{EINRAdd && EINRAdd}</td>
                    </tr>
                </tbody>
                <tbody>
                    <tr>
                        <td>2</td>
                        <td>EGOLD</td>
                        <td>{EGOLDAdd && EGOLDAdd}</td>
                    </tr>
                </tbody>
            </Table>
            </div>

            <div>
                <label><h1>Set EGOLD Add</h1></label><br />
                <input onChange={setEGOLDHandler} value={addEGOLD} />
                <button onClick={setEGOLD}>Set</button> <br />

            </div>
        </div>
    )
}
