import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import Table from 'react-bootstrap/Table';

export default function Transaction() {

  const [accounts, setAccounts] = useState(null);
  const [contract, setContract] = useState(null);
  const [EINRAdd, setEINRAdd] = useState(null);
  const [EGOLDAdd, setEGOLDAdd] = useState(null);

  const [myBalance, setMyBalance] = useState("");

  const [mint, setMint] = useState("");

  useEffect(() => {
    setTimeout(async () => {
      const artifact = require(`../contracts/EINR.json`);
      const _web3 = new Web3(Web3.givenProvider);
      const _accounts = await _web3.eth.requestAccounts();
      const networkID = await _web3.eth.net.getId();
      const { abi } = artifact;
      let address, contract;
      try {
        address = artifact.networks[networkID].address;
        contract = new _web3.eth.Contract(abi, address);
      } catch (err) {
        console.error(err);
      }
      setAccounts(_accounts);
      setContract(contract);
      setEINRAdd(address);

      _web3.eth.getPastLogs({address: _accounts[0] })
        .then(res => {
          res.forEach(rec => {
            console.log(rec.blockNumber, rec.transactionHash, rec.topics);
          });
        }).catch(err => console.log("getPastLogs failed", err));
      // await contract.methods.balanceOf(_accounts[0]).call({ from: _accounts[0] })
      //     .then(e => {
      //         //console.log(e);
      //         setMyBalance(e);
      //     })
      //     .catch(err => console.log(err));
      // await contract.methods.EGOLD().call({ from: _accounts[0] })
      //     .then(e => {
      //         //console.log(e);
      //         setEGOLDAdd(e);
      //     })
      //     .catch(err => console.log(err));
    }, 100)

  }, [])

  const getDataHandler = async () => {
    await contract.methods.balanceOf(accounts[0]).call({ from: accounts[0] })
      .then(e => {
        //console.log(e);
        setMyBalance(e);
      })
      .catch(err => console.log(err));
    await contract.methods.EGOLD().call({ from: accounts[0] })
      .then(e => {
        //console.log(e);
        setEGOLDAdd(e);
      })
      .catch(err => console.log(err));
  }

  const setMintHandler = (e) => {
    setMint(e.target.value);
  }

  const mintEINR = async () => {
    await contract.methods.mint(mint)
      .send({
        from: accounts[0]
      })
      .then(e => {
        //console.log(e);
      })
      .catch(err => console.log(err));
    getDataHandler();
    setMint("");
  }

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Contract Name</th>
            <th>Contract Address</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>EINR</td>
            <td>{EINRAdd && EINRAdd}</td>
          </tr>
        </tbody>
        <tbody>
          <tr>
            <td>2</td>
            <td>EGOLD</td>
            <td>{EGOLDAdd && EGOLDAdd}</td>
          </tr>
        </tbody>
      </Table>
      <h1>Data</h1>
      <Table striped bordered hover>
        <tbody>
          <tr>
            <th>1</th>
            <th>My Balance</th>
            <td>{myBalance}EINR</td>
          </tr>
        </tbody>
      </Table>
      <div>
        <label><h1>Mint</h1></label><br />
        <input onChange={setMintHandler} value={mint} />
        <button onClick={mintEINR}>Mint</button> <br />

      </div>
    </div>
  )
}
