import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import Table from 'react-bootstrap/Table';

export default function EGOLD() {

    const [accounts, setAccounts] = useState(null);
    const [contract, setContract] = useState(null);
    const [EINRAdd, setEINRAdd] = useState(null);
    const [EGOLDAdd, setEGOLDAdd] = useState(null);

    const [myBalance, setMyBalance] = useState("");
    const [balanceEGOLD, setBalanceEGOLD] = useState("");
    const [perEGOLD, setPerEGOLD] = useState("");
    const [totalSupply, setTotalSupply] = useState("");

    const [buy, setBuy] = useState("");

    useEffect(() => {
        setTimeout(async () => {
            const artifact = require(`../contracts/EGOLD.json`);
            const _web3 = new Web3(Web3.givenProvider);
            const _accounts = await _web3.eth.requestAccounts();
            const networkID = await _web3.eth.net.getId();
            const { abi } = artifact;
            let address, contract;
            try {
                address = artifact.networks[networkID].address;
                contract = new _web3.eth.Contract(abi, address);
            } catch (err) {
                console.error(err);
            }
            setAccounts(_accounts);
            setContract(contract);
            setEGOLDAdd(address);

            await contract.methods.balanceOf(_accounts[0]).call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setMyBalance(e);
                })
                .catch(err => console.log(err));
            await contract.methods.totalSupply().call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setTotalSupply(e);
                })
                .catch(err => console.log(err));
            await contract.methods.EGoldPrice().call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setPerEGOLD(e);
                })
                .catch(err => console.log(err));
            await contract.methods.EINR_Bal().call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setBalanceEGOLD(e);
                })
                .catch(err => console.log(err));
            await contract.methods._EINR().call({ from: _accounts[0] })
                .then(e => {
                    //console.log(e);
                    setEINRAdd(e);
                })
                .catch(err => console.log(err));
        }, 100)

    }, [])

    const getDataHandler = async () => {
        await contract.methods.balanceOf(accounts[0]).call({ from: accounts[0] })
            .then(e => {
                //console.log(e);
                setMyBalance(e);
            })
            .catch(err => console.log(err));
        await contract.methods.EGoldPrice().call({ from: accounts[0] })
            .then(e => {
                //console.log(e);
                setPerEGOLD(e);
            })
            .catch(err => console.log(err));

            await contract.methods.EINR_Bal().call({ from: _accounts[0] })
            .then(e => {
                //console.log(e);
                setBalanceEGOLD(e);
            })
            .catch(err => console.log(err));
    }

    const setBuyHandler = (e) => {
        setBuy(e.target.value);
    }

    const buyEGOLD = async () => {
        await contract.methods.buyGold(buy)
            .send({
                from: accounts[0]
            })
            .then(e => {
                //console.log(e);
            })
            .catch(err => console.log(err));
        getDataHandler();
        setBuy("");
    }

    return (
        <div>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Contract Name</th>
                        <th>Contract Address</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>1</td>
                        <td>EGOLD</td>
                        <td>{EGOLDAdd && EGOLDAdd}</td>
                    </tr>
                </tbody>
                <tbody>
                    <tr>
                        <td>2</td>
                        <td>EINR</td>
                        <td>{EINRAdd && EINRAdd}</td>
                    </tr>
                </tbody>
            </Table>
            <h1>Data</h1>
            <Table striped bordered hover>
                <tbody>
                    <tr>
                        <th>1</th>
                        <th>Total Supply</th>
                        <td>{totalSupply}EGOLD</td>
                    </tr>
                </tbody>
                <tbody>
                    <tr>
                        <th>2</th>
                        <th>Per EGOLD Price</th>
                        <td>{perEGOLD}EINR</td>
                    </tr>
                </tbody>
                <tbody>
                    <tr>
                        <th>3</th>
                        <th>EGOLD Contract EINR Balance</th>
                        <td>{perEGOLD}EINR</td>
                    </tr>
                </tbody>
                <tbody>
                    <tr>
                        <th>4</th>
                        <th>My Balance</th>
                        <td>{myBalance}EGOLD</td>
                    </tr>
                </tbody>
            </Table>
            <div>
                <label><h1>Buy</h1></label><br />
                <input onChange={setBuyHandler} value={buy} />
                <button onClick={buyEGOLD}>Buy</button> <br />

            </div>
        </div>
    )
}
